function ajuste_css(){
	$("span,label,p,pre,h1,h2,h3,h4,h5,h6").addClass("ajustemt_text_css");
	$("body,div").addClass("ajustmet_div_css");
	$("img").addClass("ajustement_img_css");
};
function remove_ajuste_css(){
	$("span,label,p,pre,h1,h2,h3,h4,h5,h6").removeClass("ajustemt_text_css");
	$("body,div").removeClass("ajustmet_div_css");
	$("img").removeClass("ajustement_img_css");
}